    private void jbGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbGuardarActionPerformed
        int codigo;
        double precio;
        int stock;
        try{
            codigo= Integer.parseInt(jtCodigo.getText());
        }catch(NumberFormatException fe){
            JOptionPane.showMessageDialog(this, "Ingrese un número en el campo del código");
            jtCodigo.setText("");
            jtCodigo.requestFocus();
            return;
        }
        String descripcion= jtDescripcion.getText();
        try{
            precio= Double.parseDouble(jtPrecio.getText()) ;
        }catch(NumberFormatException fe){
            JOptionPane.showMessageDialog(this, "Ingrese un número en el campo del precio(recuerde que para los centavos se usa un  ' . ' )");
            jtPrecio.setText("");
            jtPrecio.requestFocus();
            return;
        }
        String rubro =(String)jcRubro.getSelectedItem();
        try{
            stock= Integer.parseInt(jtStock.getText());
        }catch(NumberFormatException fe){
            JOptionPane.showMessageDialog(this, "Ingrese un número en el campo del Stock");
            jtStock.setText("");
            jtStock.requestFocus();
            return;
        }
        Productos p=new Productos(codigo, descripcion, precio, rubro, stock);
        DeTodoSA.lista.add(p);
        JOptionPane.showMessageDialog(this, "Se agregó el producto al Stock");
        limpiar();
        jtCodigo.requestFocus();
        
    }//GEN-LAST:event_jbGuardarActionPerformed

    private void jbNuevoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbNuevoActionPerformed
        limpiar();
        jtCodigo.requestFocus();
    }//GEN-LAST:event_jbNuevoActionPerformed

