    private void jbBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbBuscarActionPerformed
        int codigo=Integer.parseInt(jtCodigo.getText());
        for(Productos it:DeTodoSA.lista){
            if(it.getCodigo()==codigo){
                productoencontrado=it;
            }
        }
        if(productoencontrado!=null){
            jtDescripcion.setText(productoencontrado.getDescripcion());
            jtPrecio.setText(productoencontrado.getPrecio()+"");
            jtStock.setText(productoencontrado.getStock()+"");
            jcRubro.setSelectedItem(productoencontrado.getRubro());
        }else{
            JOptionPane.showMessageDialog(this, "No se encontró el producto en el Stock");
            productoencontrado=null;
        }
    }//GEN-LAST:event_jbBuscarActionPerformed

    private void jbEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbEliminarActionPerformed
        if(productoencontrado!=null){
            boolean resultado=DeTodoSA.lista.remove(productoencontrado);
            if(resultado=true){
                JOptionPane.showMessageDialog(this, "El producto se eliminó del Stock");
                limpiar();
                jtCodigo.requestFocus();
            }else{
                JOptionPane.showMessageDialog(this, "No se encontró el producto");
            }
        }else{
            JOptionPane.showMessageDialog(this, "ingrese un código de producto existente");
        }
    }//GEN-LAST:event_jbEliminarActionPerformed

    private void jbModificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbModificarActionPerformed
        if (productoencontrado != null) {
            int codigo;
            double precio;
            int stock;
 
            try{
                codigo= Integer.parseInt(jtCodigo.getText());
            }catch(NumberFormatException fe){
                JOptionPane.showMessageDialog(this, "Ingrese un número en el campo del código");
                jtCodigo.setText("");
                jtCodigo.requestFocus();
                return;
        }
        String descripcion = jtDescripcion.getText();
        try{
            precio = Double.parseDouble(jtPrecio.getText());
        }catch(NumberFormatException fe){
            JOptionPane.showMessageDialog(this, "Ingrese un número en el campo del precio(recuerde que para los centavos se usa un  ' . ' )");
            jtPrecio.setText("");
            jtPrecio.requestFocus();
            return;
        }
        String rubro = (String) jcRubro.getSelectedItem();
        try{
            stock = Integer.parseInt(jtStock.getText());
        }catch(NumberFormatException fe){
            JOptionPane.showMessageDialog(this, "Ingrese un número en el campo del Stock");
            jtStock.setText("");
            jtStock.requestFocus();
            return;
        }
        
        productoencontrado.setDescripcion(descripcion);
        productoencontrado.setPrecio(precio);
        productoencontrado.setRubro(rubro);
        productoencontrado.setStock(stock);

        JOptionPane.showMessageDialog(this, "Producto modificado exitosamente");
        limpiar();
    } else {
        JOptionPane.showMessageDialog(this, "No se ha encontrado un producto para modificar");
    }
    }//GEN-LAST:event_jbModificarActionPerformed

     private void limpiar(){
            jtCodigo.setText("");
            jtDescripcion.setText("");
            jtPrecio.setText("");
            jtStock.setText("");
        }

